function updateColorDisplays(t,a,e,o){let r=`rgba(${t}, ${a}, ${e}, ${o})`,s=`#${t.toString(16).padStart(2,"0")}${a.toString(16).padStart(2,"0")}${e.toString(16).padStart(2,"0")}`.toUpperCase();$("#hex-preview").fadeOut(150,(function(){$(this).css("backgroundColor",r),$(this).text(s),$(this).fadeIn(150)}));let n=`rgba(${t}, ${a}, ${e}, ${o})`;$("#rgba-preview").fadeOut(150,(function(){$(this).css("backgroundColor",r),$(this).text(n),$(this).fadeIn(150)}));let i=rgbToHsl(t,a,e),h=`hsla(${Math.round(i.hue)}, ${Math.round(i.saturation)}%, ${Math.round(i.lightness)}%, ${o})`;$("#hsla-preview").fadeOut(150,(function(){$(this).css("backgroundColor",r),$(this).text(h),$(this).fadeIn(150)})),setTimeout((()=>{adjustTextContrast()}),300)}function rgbToHsl(t,a,e){t/=255,a/=255,e/=255;let o,r,s=Math.max(t,a,e),n=Math.min(t,a,e),i=(s+n)/2;if(s==n)o=r=0;else{let h=s-n;switch(r=i>.5?h/(2-s-n):h/(s+n),s){case t:o=(a-e)/h+(a<e?6:0);break;case a:o=(e-t)/h+2;break;case e:o=(t-a)/h+4}o/=6}return{hue:360*o,saturation:100*r,lightness:100*i}}function adjustTextContrast(){$(".color-preview").each((function(){let t=$(this).css("backgroundColor").match(/\d+/g);if(t&&t.length>=3){let a=(299*parseInt(t[0])+587*parseInt(t[1])+114*parseInt(t[2]))/1e3;$(this).css("color",a>128?"black":"white")}}))}updateColorDisplays(255,255,255,1),$("#generateColor").click((function(){updateColorDisplays(Math.floor(255*Math.random()),Math.floor(255*Math.random()),Math.floor(255*Math.random()),Math.random().toFixed(2))}));